import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import EditableField from "../EditableField/EditableField";

import "./ReaderView.css";

class ConnectedReaderView extends Component {
  constructor(props) {
    super(props);

    this.state = {
			script: null,
      animateGreeting: "ReaderView-animategreeting",
    };
  }

  componentDidMount() {
    let scriptId = window.location.pathname.replaceAll("/readerview/", "")

    if(scriptId != ""){
      Firebase.getScriptById(scriptId)
      .then(val => {
        this.setState({ script: val })
      })
    }
  }

  getScriptName = (name) => {
    return name[1]
  }

  render() {
		if(this.state.script){
			return (
        <div className="ReaderView">
					<div className="ReaderView-container l-container">
							<div className="ReaderView-title">
								<EditableField getScriptName={this.getScriptName}/>
							</div>
					</div>
        </div>
    	);
		}else{
			return (
        <div className="ReaderView">
					loading script
        </div>
    	);
		}
  }
}

const mapStateToProps = (state) => {
  return {};
};

let ReaderView = withRouter(connect(mapStateToProps)(ConnectedReaderView));
export default withRouter(ReaderView);
