import { DataArray } from "@mui/icons-material";
import React, { Component } from "react";
import firebase from "../../firebase/firebase";

class MessageNode {
  constructor(data) {
    this.data = data;
    this.next = null;
  }
}

class Script {
  constructor(name) {
    var milliseconds = Math.floor(Date.now() / 1000)

    this.name = name;
    this.dateCreated = milliseconds
    this.id = name + this.dateCreated
    this.cast = [];
    this.crew = [];
    this.head = null;
    this.messages = new MessageNode();
    this.node = {};
    this.totalNoMsgs = 0
  }

  UpdateScriptName(name){
    this.name = name
    this.id = this.name + this.dateCreated
  }

  GetScriptName(){
    return this.name
  }

  NumberOfMessages() {
    let count = 0;
    let node = this.head;
    while (node) {
      count++;
      node = node.next;
    }
    return count;
  }

  deleteScript() {
    this.head = null;
  }

  getLastMessage() {
    let lastNode = this.head;
    if (lastNode) {
      while (lastNode.next) {
        lastNode = lastNode.next;
      }
    }
    return lastNode;
  }

  getFirstMessage() {
    return this.head;
  }

  addNewMessage(data) {
    this.totalNoMsgs += 1
    data.MsgIndex = this.totalNoMsgs

    let newNode = new MessageNode(data);

    if (!this.head) {
      this.head = newNode;
      return this.head;
    }

    let current = this.head;
    while (current.next) {
      current = current.next;
    }
    current.next = newNode;
  }

  deleteMessage(id){
    if (this.head.data.id === id) {
      this.head = this.head.next;
    } else {
      let current = this.head;
      while (current.next) {
        if (current.next.data.id === id) {
          current.next = current.next.next;
          break;
        }
        current = current.next;
      }
    }
    return this.head;
  }

  getLastMessage() {
    let current = this.head;

    while (current.next) {
      current = current.next;
    }

    return current.data.content
  }

  getNthMessage(n) {
    n -= 1
    let current = this.head;

    for(var i = 0; i < n; i++){
      if (current){
        current = current.next;
      }else{
        //nth message doesn't exist
        return ""
      }
    }

    return current.data.content
  }

  getNthMessageNode(n) {
    n -= 1
    let current = this.head;

    for(var i = 0; i < n; i++){
      if (current.next){
        current = current.next;
      }else{
        //nth message doesn't exist
        return ""
      }
    }

    return current.data
  }

  getAllMessagesStringList() {
    let messages = []
    let current = this.head;

    while (current) {
      messages.push(current.data.content)
      current = current.next;
    }

    return messages
  }

  getAllMessagesStringListBySenderId(senderId){
    let messages = []
    let current = this.head;

    while (current) {
      if (current.data.senderId == senderId){
        messages.push(current.data)
      }
      current = current.next;
    }

    return messages
  }

  getAllMessagesAsNodes() {
    let messages = []
    let current = this.head;

    while (current) {
      messages.push(current.data)
      current = current.next;
    }

    return messages
  }

  getSenderNameFromID(senderId){
    const mergedArray = this.getAllCast().concat(this.getAllCrew());
    const sender = mergedArray.find(obj => obj.id === senderId);
    return sender.name
  }

  sendScriptToFirebase() {
    let script = {
      id: this.id,
      name: this.name,
      dateCreated: this.dateCreated,
      cast: this.getAllCast(),
      crew: this.getAllCrew(),
      messages: this.getAllMessagesAsNodes(),
    }

    firebase.createNewScript(script)
  }

  updateCast(cast){
    this.cast = cast
  }

  updateScriptFirebase(){
    let script = {
      id: this.id,
      name: this.name,
      dateCreated: this.dateCreated,
      cast: this.getAllCast(),
      crew: this.getAllCrew(),
      messages: this.getAllMessagesAsNodes(),
    }

    firebase.updateScript(script)
  }

  removeCast(cast){
    let newCast = []

    this.cast.forEach((existingCast) => {
      if (existingCast.id != cast.id){
        newCast.push(existingCast)
      }
    })

    this.cast = newCast
  }

  addNewCrew(crew){
    this.crew.push(crew)
  }

  getAllCast(){
    return this.cast
  }

  getAllCrew(){
    return this.crew
  }

  removeCrew(crew){
    let newCrew = []

    this.crew.forEach((existingCrew) => {
      if (existingCrew.id != crew.id){
        newCrew.push(existingCrew)
      }
    })

    this.crew = newCrew
  }

  getScriptName() {
    return this.name;
  }

  updateScriptName(name) {
    this.name = name;
  }

  grabScriptFromFirebase(scriptId){
    return new Promise(resolve => {
      firebase.getScriptById(scriptId)
      .then(val => {

        let messages = val.messages
        for(var i=0; i<messages.length; i++){
          this.addNewMessage(messages[i])
        }

        this.name = val.name;
        this.dateCreated = val.dateCreated
        this.id = val.id
        this.cast = val.cast ? val.cast : []
        this.crew = val.crew ? val.crew : []
      })
      .then(() => {
        resolve(this)
      })
    })
  }

  getNodeByMessageId(messageId){
    let currentNode = this.head;
    while (currentNode) {
      if (currentNode.id === messageId) {
        return currentNode;
      }
      currentNode = currentNode.next;
    }
    return null;
  }

  likeMessage(messageId, selectedCastID){
    let message = this.getNodeByMessageId(messageId)
    console.log("message", message, messageId, selectedCastID)
    message.data.LikedBy.push(selectedCastID)
  }
}

export default Script;
