import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import Script from "../Script/Script.js";
import EditableField from "../EditableField/EditableField";
import CastMembers from "../CastMembers/CastMembers";
import MsgTypes from "../MsgTypes/MsgTypes";
import Button from "@material-ui/core/Button";

var textyng = new Script();

import "./NewScript.css";

class ConnectedNewScript extends Component {
  constructor(props) {
    super(props);

    this.state = {
      rimis: [],
      rimisIds: [],
      filteredRimis: [],
      rimiToShow: null,
      animateGreeting: "NewScript-animategreeting",
      orderId: "",
      script: textyng,
      selectedCastId: "",
      selectedMsgType: "",
    };

  }

  componentDidMount() {

    // this.state.script.updateScriptName("cary me dey go my husband");
    // this.state.script.getScriptName();

    // this.state.script.addNewMessage({
    //   id: "timedateformat",
    //   timeSent: "",
    //   type: "text",
    //   content: "i saw kike with samuel yesterday! eyin girls",
    // });
  }

  getScriptName = (name) => {
    return name[1]
  }

  getAllCast = (casts) => {
    console.log("casts", casts)
  }

  getSelectedCast = (cast) => {
    this.setState({selectedCastId: cast.id})
  }

  getSelectedMsgType = (value) => {
    this.setState({selectedMsgType: value.target.innerText})
  }

  render() {
    return (
        <div className="NewScript">
            <div className="NewScript-container l-container">
                <div className="NewScript-title">
                    <EditableField getScriptName={this.getScriptName} getSelectedCast/>
                </div>
                <div className="NewScript-cast">
                    <CastMembers selectedCast={() =>  this.getSelectedCast} getAllCast={this.getAllCast}/>
                </div>
                <div className="NewScript-textView">
                    <MsgTypes selectedMsgType={() =>  this.getSelectedMsgType} />

                    <div className="NewScript-chatArea">

                    </div>

                    <div className="NewScript-sendMessage">
                      <TextField
                        variant="outlined"
                        className="NewScript-sendMessage--input"
                        value={this.state.orderId}
                        placeholder="Type message here"
                        onChange={e => {
                            this.setState({ orderId: e.target.value });
                        }}
                      />
                      <Button
                        className="NewScript-sendMessage--send"
                        variant="outlined"
                        color="primary"
                        onClick={() => {
                            this.refreshTable()
                        }}
                      >
                        Send
                      </Button>
                    </div>
                </div>
            </div>
        </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {};
};

let NewScript = withRouter(connect(mapStateToProps)(ConnectedNewScript));
export default withRouter(NewScript);
