import React from "react";

import "./MsgTypes.css";

function MsgTypes(props) {
  let msgTypes = [
    "Screen Shot",
    "Is Typing",
    "Sending To Group",
    "Insert Image",
    "Voice Note",
    "External Chat",
  ];

  function updateActiveOption(value, index) {
    var el = document.querySelectorAll('.controllers span');
for (let i = 0; i < el.length; i++) {
  el[i].onclick = function() {
    var c = 0;
    while (c < el.length) {
      el[c++].className = 'slide';
    }
    el[i].className = 'slide active';
  };
}

    props.selectedMsgType ? props.selectedMsgType(value) : {};
  }

  return (
    <div className="MsgTypes">
      {msgTypes.map((value, index) => (
        <div
          id={"MsgTypes-id-" + index}
          className={isActive ? "MsgTypes-name active" : "MsgTypes-name"}
          key={index}
          onClick={() => updateActiveOption(value, index)}
        >
          {value}
        </div>
      ))}
    </div>
  );
}

export default MsgTypes;
