import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import Script from "../Script/Script.js";
import { Emoji } from 'emoji-mart'

import "./ReaderView.css";

const emojis = [
  {name: "exploding_head", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
  {name: "relaxed", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
  {name: "astonished", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
  {name: "pensive", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
  {name: "angry", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
  {name: "scream", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
  {name: "kissing_heart", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
  {name: "rolling_on_the_floor_laughing", set: "twitter", size: 24, className: "ReaderView-reactionEmoji"},
]

class ConnectedReaderView extends Component {
  constructor(props) {
    super(props);

    this.state = {
			script: null,
      allMessages: [],
      currentNodeIndex: 1,
      selectedSceneId: 1,
      timeoutId: null,
      isPlaying: true,
    };
  }

  componentDidMount() {
    let scriptId = window.location.pathname.replaceAll("/readerview/", "")

      if(scriptId != ""){
        var textyng = new Script(scriptId);
        textyng.grabScriptFromFirebase(scriptId)
        .then(() => {
          this.setState({
            script: textyng,
            allMessages: textyng.getAllMessagesAsNodes().slice(0,1),
            currentNodeIndex: 1,
          })
        })
      }
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.currentNodeIndex !== this.state.currentNodeIndex && this.state.isPlaying) {
      const nextNode = this.state.script.getNthMessageNode(this.state.currentNodeIndex);
      this.setState({
        timeoutId: setTimeout(() => {
          this.setState({
            allMessages: this.state.script.getAllMessagesAsNodes().slice(0, this.state.currentNodeIndex),
            selectedSceneId: nextNode.sceneId || this.state.script.getTotalNumScenes()
          });
        }, nextNode.tslmsg * 100)
      });
    }
  }

  handlePreviousClick = () => {
    clearTimeout(this.state.timeoutId);
    let prevNodeIndex = this.state.currentNodeIndex - 1;
    if (prevNodeIndex < 0) {
        prevNodeIndex = 0;
    }
    this.updateCurrentNode(prevNodeIndex);
  }

  handlePlayPauseClick = () => {
    // code to handle play/pause button click
    this.setState(prevState => {
      if (prevState.isPlaying) {
        clearTimeout(this.timeoutId);
      } else if(!prevState.isPlaying) {
        this.updateCurrentNode();
        let nextNode = this.state.script.getNthMessageNode(this.state.currentNodeIndex);
        this.timeoutId = setTimeout(() => {
          this.updateCurrentNode(this.state.currentNodeIndex + 1);
        }, nextNode.tslmsg * 1000);
      }
      return { isPlaying: !prevState.isPlaying };
    });
  };

  handleNextClick = () => {
    clearTimeout(this.state.timeoutId);
    let nextNodeIndex = this.state.currentNodeIndex + 1;
    let totalNumNodes = this.state.script.getAllMessagesAsNodes().length;
    if (nextNodeIndex > totalNumNodes - 1) {
      nextNodeIndex = totalNumNodes - 1;
    }
    this.updateCurrentNode(nextNodeIndex);
  }

  updateCurrentNode(index) {
    if (index === undefined) {
        index = this.state.currentNodeIndex + 1;
    }
    if (index >= 0 && index < this.state.script.getAllMessagesAsNodes().length) {
      let sceneId = this.state.script.getNthMessageNode(index).sceneId;
      if (sceneId === undefined) {
          sceneId = this.state.script.getTotalNumScenes();
      }
      this.setState({
          currentNodeIndex: index,
          allMessages: this.state.script.getAllMessagesAsNodes().slice(0, index),
          selectedSceneId: sceneId
      });
    }
  }

  getScriptName = (name) => {
    return name[1]
  }

  selectThisScene = (sceneId) => {
    this.setState({
      selectedSceneId: sceneId,
      allMessages: this.state.script.getAllMessagesAsNodes(),
    })
  }

  reactionClicked = (emojiName, messageId, event) => {
    event.currentTarget.classList.add('shake');
    this.state.script.updateReaderReaction(emojiName, messageId)
  }

  getReaderReactionFromMsg = () => {
    let reactionArray = [];
    this.state.allMessages.forEach(message => {
      if(this.state.script.getReaderReactionMap().has(message.id.toString())) {
        reactionArray.push(...this.state.script.getReaderReactionMap().get(message.id.toString()));
      }
    });

    let readerEmojiReactions = reactionArray.map((emoji, index) => (
      <Emoji
        emoji={emoji}
        set={"twitter"}
        size={24}
        key={index}
        className="ReaderView-reactionEmoji"
      />
    ));

    // console.log("readerEmojiReactions", readerEmojiReactions)

    return readerEmojiReactions
  }

  render() {
		if(this.state.allMessages && this.state.allMessages.length > 0){
			return (
        <div className="ReaderView">
          <div className="ReaderView-container l-container">
            <div className="ReaderView-title">
              {this.state.script.getScriptName()}
            </div>
            <div className="ReaderView-scenes">
              {this.state.script.getScenes()
              .map((scene, index) =>
                scene.id == this.state.selectedSceneId ? (
                  <div key={index} onClick={() => this.selectThisScene(scene.id)} className="ReaderView-scene ReaderView-scene--currentScene">
                    {scene.name}
                  </div>
                ) : (
                  <div key={index} onClick={() => this.selectThisScene(scene.id)} className="ReaderView-scene">
                    {scene.name}
                  </div>
                )
              )}
            </div>
            <div className="ReaderView-textView">
              <div id="ReaderView-chatArea-id" className="ReaderView-chatArea">
                {this.state.allMessages
                .map((message, index) => (
                  (message.sceneId == this.state.selectedSceneId)
                  &&
                  <div key={index} className="ReaderView-chatArea-msg">
                    {message.isImg &&
                      <img
                        className="ReaderView-imgMsg"
                        src={message.url}
                      />
                    }
                    {message.isAudio &&
                      <audio
                        className="ReaderView-audioMsg"
                        src={message.url}
                        controls
                      />
                    }
                    {message.isVideo &&
                      <video
                        className="ReaderView-videoMsg"
                        src={message.url}
                        controls
                      />
                    }
                    <div>
                      <span>{message.content}</span>
                      <span className="ReaderView-senderName">{this.state.script.getSenderNameFromID(message.senderId)}</span>

                      <div className="ReaderView-reactionEmojis">
                        {emojis.map((emoji, index)=> 
                          <div
                            key={index}
                            className="ReaderView-reactionEmoji"
                            onClick={(e) => this.reactionClicked(emoji.name, message.id, e)}
                          >
                            <Emoji 
                              emoji={emoji.name}
                              set={emoji.set} 
                              size={emoji.size} 
                              key={emoji.name} 
                              className="ReaderView-reactionEmoji"
                            />
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="ReaderView-readerReaction--wrapper">
              <span>reactions:</span> 
              <span className="ReaderView-readerReaction"> {this.getReaderReactionFromMsg()}</span>                       
            </div>
            <div className="ReaderView-navigation">
              <div className="ReaderView-navigation--wrapper">
                <div className="previous-button" onClick={this.handlePreviousClick}>
                  <i className="fas fa-arrow-left"></i>
                </div>
                <div className="play-pause-button" onClick={this.handlePlayPauseClick}>
                  {this.state.isPlaying ? 
                    <i className="fas fa-pause"></i> : 
                    <i className="fas fa-play"></i>
                  }
                </div>
                <div className="next-button" onClick={this.handleNextClick}>
                  <i className="fas fa-arrow-right"></i>
                </div>
              </div>
            </div>
          </div>
        </div>
    	);
		}else{
			return (
        <div className="ReaderView">
					loading scriptsss
        </div>
    	);
		}
  }
}

const mapStateToProps = (state) => {
  return {};
};

let ReaderView = withRouter(connect(mapStateToProps)(ConnectedReaderView));
export default withRouter(ReaderView);
