import React, { Component } from "react";

class MessageNode {
  constructor(data) {
    this.data = data
    this.next = null
  }
}

class Script {
  constructor(name) {
    this.name = name
    this.head = null
    this.messages = new(MessageNode)
    this.node = {};
  }

  NumberOfMessages() {
    let count = 0;
    let node = this.head;
    while (node) {
      count++;
      node = node.next;
    }
    return count;
  }

  deleteScript() {
    this.head = null;
  }

  getLastMessage() {
    let lastNode = this.head;
    if (lastNode) {
        while (lastNode.next) {
            lastNode = lastNode.next
        }
    }
    return lastNode
  }

  getFirstMessage() {
    return this.head;
  }

  addNewMessage(data) {
    let newNode = new MessageNode(data)

    if (!this.head) {
      this.head = newNode
      return this.head
    }

    let current  = this.head
    while (current.next) {
      current = current.next
    }
    current.next = newNode
  }

  getScriptName(){
    console.log(this.name)
  }

  updateScriptName(name){
    this.name = name
  }
}

export default Script;
