import React, { useState } from 'react';
import TextField from "@material-ui/core/TextField";

import "./CommentedPopup.css"

function CommentedPopup() {
  const [isVisible, setIsVisible] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const handleShow = () => {
    setIsVisible(true);
  };

  const handleSave = () => {
    props.onSave(inputValue);
    setIsVisible(false);
  };

  const handleClose = () => {
    setIsVisible(false);
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  return (
    <div className="parent-element">
      {isVisible && (
        <div id="popup">
          <div id="popup-content">
            <p>Enter your comment:</p>
            <TextField
              variant="outlined"
              value={comment}
              placeholder="Type comment here"
              onChange={e => handleInputChange(e)}
              multiline
              rows={4}
            />
            <button id="save" onClick={handleSave}>Save</button>
            <button id="close" onClick={handleClose}>&times;</button>
          </div>
        </div>
      )}
      <button id="commented-button" onClick={handleShow}>Commented</button>
    </div>
  );
}

export default CommentedPopup;
