import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import Script from "../Script/Script.js";
import Typography from "@material-ui/core/Typography";

var textyng = new Script();

import "./NewScript.css";

class ConnectedNewScript extends Component {
  constructor(props) {
    super(props);

    this.state = {
      rimis: [],
      rimisIds: [],
      filteredRimis: [],
      rimiToShow: null,
      animateGreeting: "NewScript-animategreeting",
      orderId: "",
      script: textyng,
    };

  }

  componentDidMount() {


    const [scriptName, setScriptName] = React.useState("John Snow");
    const [isNameFocused, setIsNamedFocused] = React.useState(false);

    // this.state.script.updateScriptName("cary me dey go my husband");
    // this.state.script.getScriptName();

    // this.state.script.addNewMessage({
    //   id: "timedateformat",
    //   timeSent: "",
    //   type: "text",
    //   content: "i saw kike with samuel yesterday! eyin girls",
    // });
  }

  getName = (name) => {
    console.log("name", name)
  }

  render() {
    return (
        <div className="NewScript">
            <div className="NewScript-container l-container">
                <div className="NewScript-title">
                    <EditableField getName={this.getName} />
                </div>

                <div className="NewScript-cast">

                </div>
                <div className="NewScript-textView">
                    <div className="NewScript-msgTypes">
                        {/* message type pane */}
                    </div>

                    <div className="NewScript-chatArea">
                        {/* chat area */}
                    </div>

                    <div className="NewScript-sendMessage">
                        <TextField
                            className="NewScript-sendMessage"
                            value={this.state.orderId}
                            placeholder="Type message here"
                            onChange={e => {
                                this.setState({ orderId: e.target.value });
                            }}
                        />
                        <div>
                            send
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {};
};

let NewScript = withRouter(connect(mapStateToProps)(ConnectedNewScript));
export default withRouter(NewScript);
