import React from "react";
import EditableField from "../EditableField/EditableField";

import "./CastMembers.css";

function CastMembers(props) {
  let totalCast = 2
  let defaultCast = [
    {
      name: "Jesuye David",
      id: 1,
    },
    {
      name: "Cast Member",
      id: 2,
    },
  ]

  props.getAllCast(casts)

  const [casts, setCast] = React.useState(defaultCast);

  function addNewCastMember(){
    totalCast +=1

    let newCast = {
      name: "Cast Member",
      id: totalCast
    }

    setCast(casts => [...casts, newCast])
  }

  function udpateCastName(name){
    let oldName = name[0]
    let newName = name[1]

    for(var i =0; i<casts.length; i++){
      if(casts[i].name == oldName){
        casts[i].name = newName
      }
    }

    console.log("casts", casts)
    setCast(casts)
  }

  return (
    <div className="CastMembers">
      {casts.map(
        (value, index) => (
          <div id={"CastMembers-cast-" + value.id} className="CastMembers-cast" key={index}>
            <EditableField name={value.name} getScriptName={udpateCastName} />
          </div>
        )
      )}

      <div className="CastMembers-addCast CastMembers-cast" onClick={() => addNewCastMember()}>
        +
      </div>
    </div>
  );
}

export default CastMembers;
