import React, { Component } from "react";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import Script from "../Script/Script.js";

import "./Advertisement.css";

class ConnectedAdvertisement extends Component {
  constructor(props) {
    super(props);

    this.state = {
		script: null,
    };
  }

  componentDidMount() {
    let scriptId = window.location.pathname.replaceAll("/Advertisement/", "")

    if(scriptId != ""){
      var textyng = new Script(scriptId);
      textyng.grabScriptFromFirebase(scriptId)
      .then(() => {
        this.setState({ script: textyng })
      })
    }
  }

  componentDidUpdate(){
    //
  }

  getScriptName = (name) => {
    return name[1]
  }

  render() {
		if(this.state.script && this.state.script.getAllMessagesAsNodes()){
			return (
        <div className="Advertisement">
            <div className="Advertisement-container l-container">
                <div className="Advertisement-title">
                  {this.state.script.GetScriptName()}
                </div>
                <div className="Advertisement-textView">
                  <div id="Advertisement-chatArea-id" className="Advertisement-chatArea">
                    {this.state.script.getAllMessagesAsNodes().map((value,index) => (
                      <div key={index} className="Advertisement-chatArea-msg">
                        {value.content}
                      </div>
                    ))}
                  </div>
                </div>
            </div>
        </div>
    	);
		}else{
			return (
        <div className="Advertisement">
					loading scriptsss
        </div>
    	);
		}
  }
}

const mapStateToProps = (state) => {
  return {};
};

let Advertisement = withRouter(connect(mapStateToProps)(ConnectedAdvertisement));
export default withRouter(Advertisement);
