import React, { Component } from "react";
import firebase from "../../firebase/firebase";

class MessageNode {
  constructor(data) {
    this.data = data;
    this.next = null;
  }
}

class Script {
  constructor(name) {
    this.name = name;
    this.id = name + new Date().toDateString()
    this.cast = [];
    this.crew = [];
    this.head = null;
    this.messages = new MessageNode();
    this.node = {};
  }

  NumberOfMessages() {
    let count = 0;
    let node = this.head;
    while (node) {
      count++;
      node = node.next;
    }
    return count;
  }

  deleteScript() {
    this.head = null;
  }

  getLastMessage() {
    let lastNode = this.head;
    if (lastNode) {
      while (lastNode.next) {
        lastNode = lastNode.next;
      }
    }
    return lastNode;
  }

  getFirstMessage() {
    return this.head;
  }

  addNewMessage(data) {
    let newNode = new MessageNode(data);

    if (!this.head) {
      this.head = newNode;
      return this.head;
    }

    let current = this.head;
    while (current.next) {
      current = current.next;
    }
    current.next = newNode;
  }

  getLastMessage() {
    let current = this.head;

    while (current.next) {
      current = current.next;
    }

    return current.data.content
  }

  getNthMessage(n) {
    n -= 1
    let current = this.head;

    for(var i = 0; i < n; i++){
      if (current){
        current = current.next;
      }else{
        //nth message doesn't exist
        return ""
      }
    }

    return current.data.content
  }

  getAllMessagesStringList() {
    let messages = []
    let current = this.head;

    while (current) {
      messages.push(current.data.content)
      current = current.next;
    }

    return messages
  }

  getAllMessagesAsNodes() {
    let messages = []
    let current = this.head;

    while (current) {
      messages.push(current.data)
      current = current.next;
    }

    return messages
  }

  sendScriptToFirebase() {
    let script = {
      id: this.id,
      name: this.name,
      timeSent: script.timeSent,
      type: script.type,
      content: script.content,
    }

    firebase.createNewScript(script)
  }

  addNewCast(cast){
    this.cast.push(cast)
  }

  removeCast(cast){
    let newCast = []

    this.cast.forEach((existingCast) => {
      if (existingCast.id != cast.id){
        newCast.push(existingCast)
      }
    })

    this.cast = newCast
  }

  addNewCrew(crew){
    this.crew.push(crew)
  }

  removeCrew(crew){
    let newCrew = []

    this.crew.forEach((existingCrew) => {
      if (existingCrew.id != crew.id){
        newCrew.push(existingCrew)
      }
    })

    this.crew = newCrew
  }

  getScriptName() {
    return this.name;
  }

  updateScriptName(name) {
    this.name = name;
  }
}

export default Script;
