import React, { useState } from 'react';

import "./CommentedPopup.css"

function CommentedPopup() {
  const [isVisible, setIsVisible] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const handleShow = () => {
    setIsVisible(true);
  };

  const handleSave = () => {
    props.onSave(inputValue);
    setIsVisible(false);
  };

  const handleClose = () => {
    setIsVisible(false);
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  return (
    <div className="parent-element">
      {isVisible && (
        <div id="popup">
          <div id="popup-content">
            <p>Enter your comment:</p>
            <input type="text" value={inputValue} onChange={handleInputChange} />
            <button id="save" onClick={handleSave}>Save</button>
            <button id="close" onClick={handleClose}>Close</button>
          </div>
        </div>
      )}
      <button id="commented-button" onClick={handleShow}>Commented</button>
    </div>
  );
}

export default CommentedPopup;
