import React from "react";

import "./MsgTypes.css";

function MsgTypes(props) {
  let msgTypes = [
    "Screen Shot",
    "Is Typing",
    "Sending To Group",
    "Insert Image",
    "Voice Note",
    "External Chat",
  ];

  function updateActiveOption(value, index) {
    var el = document.querySelectorAll(".MsgTypes-name");

    for (let i = 0; i < el.length; i++) {
      el[i].classList.remove("active")
    }

    document.getElementById("MsgTypes-id-" + index).classList.add("active")

    props.selectedMsgType ? props.selectedMsgType(value) : {};
  }

  return (
    <div className="MsgTypes">
      {msgTypes.map((value, index) => (
        <div
          id={"MsgTypes-id-" + index}
          className={"MsgTypes-name"}
          key={index}
          onClick={() => updateActiveOption(value, index)}
        >
          {value}
        </div>
      ))}
    </div>
  );
}

export default MsgTypes;
