import React, { Component } from "react";
import TextField from "@material-ui/core/TextField";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import EditableField from "../EditableField/EditableField";

import "./ReaderView.css";

class ConnectedReaderView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      rimis: [],
      rimisIds: [],
      filteredRimis: [],
      rimiToShow: null,
      animateGreeting: "ReaderView-animategreeting",
      orderId: "",
      script: textyng,
      selectedCastId: 1,
      selectedMsgType: "",
      timeSinceLastMsg: "",
      textMsg: "",
      allMessages: [],
      spslm: 0,
    };
  }

  componentDidMount() {
    //
  }

  render() {
    return (
        <div className="ReaderView">
            <div className="ReaderView-container l-container">
                <div className="ReaderView-title">
                    <EditableField getScriptName={this.getScriptName}/>
                </div>
            </div>
        </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {};
};

let ReaderView = withRouter(connect(mapStateToProps)(ConnectedReaderView));
export default withRouter(ReaderView);
