import React from 'react';
import { Recorder, Waveform } from 'react-record';

import './RecordModal.css';

// Initialize Firebase
const firebaseConfig = {
  // Your Firebase config goes here
};
firebase.initializeApp(firebaseConfig);

// Get a reference to the storage bucket
const storage = firebase.storage();
const bucket = storage.ref();

function RecordModal() {
  const [isRecording, setIsRecording] = React.useState(false);
  const [audioUrl, setAudioUrl] = React.useState(null);

  function handleStartRecording() {
    setIsRecording(true);
  }

  function handleStopRecording(blob) {
    setIsRecording(false);
    setAudioUrl(blob.blobURL);

    // Upload the audio to Firebase
    const audioFile = new File([blob.blob], 'audio.mp3', {
      type: 'audio/mp3'
    });
    bucket.put(audioFile).then(snapshot => {
      console.log('Audio uploaded to Firebase');
    });
  }

  return (
    <div className="record-modal">
      {isRecording ? (
        <Recorder onStop={handleStopRecording} />
      ) : (
        <button onClick={handleStartRecording} className="record-button">
          Start recording
        </button>
      )}
      {audioUrl && (
        <div>
          <audio src={audioUrl} controls />
          <Waveform audioUrl={audioUrl} width={500} height={100} />
        </div>
      )}
    </div>
  );
}
